mapper.select:
SELECT d.deviceid,d.port,d.number,d.time,d.error,d.count,d.data, dg.name AS groupname,r.name AS roomname,l.name AS locationname,CONCAT(p.`name`,c.`name`,a.`name`) AS addressname FROM `${condition.tableName}` d LEFT JOIN device de ON d.deviceid = de.id LEFT JOIN devicegroup dg ON de.groupid = dg.id LEFT JOIN room r ON dg.roomid = r.id LEFT JOIN location l ON r.locationid = l.id LEFT JOIN area a ON l.areaid = a.areaid LEFT JOIN city c ON c.cityid = a.cityid LEFT JOIN province p ON p.provinceid = c.provinceid
WHERE d.deviceid = ${condition.deviceId} AND time BETWEEN '${condition.startTime}' AND '${condition.endTime}' AND (UNIX_TIMESTAMP(time)-UNIX_TIMESTAMP('${condition.startTime}'))%${condition.timeStep} = 0 ORDER BY time
LIMIT ${page.currentNumber},${page.numberPerPage}
