mapper.update:
UPDATE devicegroup
name = '${name}', pic = '${pic}', describe = '${describes}',
WHERE id = ${groupId}

mapper.select:
SELECT d.id,d.groupid,d.termid,d.status,d.port,d.number,d.name,d.describes, t.count,t.protocol,p.name AS termtype,t.name AS termname FROM device d LEFT JOIN deviceterm t ON d.termid = t.id LEFT JOIN devicetype p ON t.type = p.id
d.groupid = ${_parameter}
ORDER BY d.groupid,d.id
