mapper.update:
UPDATE location
systemid = ${systemId}, provinceid = ${provinceId}, cityid = ${cityId}, areaid = ${areaId}, positionx = ${positionX}, positiony = ${positionY}, name = '${name}', describes = '${describes}',
WHERE id = ${id}

mapper.select:
SELECT
vw.id,vw.systemid,vw.provinceid,vw.cityid,vw.areaid,vw.positionx,vw.positiony,vw.name,vw.describes,
vw.provincename,vw.cityname,vw.areaname,vw.systemname,vw.pic
FROM (SELECT
l.id,l.systemid,l.provinceid,l.cityid,l.areaid,l.positionx,l.positiony,l.name,l.describes,
p.name AS provincename,c.name AS cityname,a.name AS areaname,s.name AS systemname,s.pic AS pic

FROM location l
LEFT JOIN province p ON l.provinceid = p.provinceid
LEFT JOIN city c ON l.cityid = c.cityid
LEFT JOIN area a ON l.areaid = a.areaid
LEFT JOIN system s ON l.systemid = s.id


        l.systemid = ${limit.systemId}
        AND l.provinceid = ${limit.provinceId}
        AND l.cityid = ${limit.cityId}
        AND l.areaid = ${limit.areaId}


ORDER BY
l.systemid,l.provinceid,l.cityid,l.areaid ) vw

    AND vw.systemid = ${systemId}


    LIMIT ${page.currentNumber},${page.numberPerPage}

SELECT count(0)
FROM ( SELECT
l.id,l.systemid
FROM location l
LEFT JOIN province p ON l.provinceid = p.provinceid
LEFT JOIN city c ON l.cityid = c.cityid
LEFT JOIN area a ON l.areaid = a.areaid
LEFT JOIN system s ON l.systemid = s.id


        l.systemid = ${limit.systemId}
        AND l.provinceid = ${limit.provinceId}
        AND l.cityid = ${limit.cityId}
        AND l.areaid = ${limit.areaId}


) vw

    AND vw.systemid = ${systemId}

SELECT vw.id,vw.systemid,vw.provinceid,vw.cityid,vw.areaid,vw.positionx,vw.positiony,vw.name,vw.describes, vw.provincename,vw.cityname,vw.areaname,vw.systemname,vw.pic FROM (SELECT l.id,l.systemid,l.provinceid,l.cityid,l.areaid,l.positionx,l.positiony,l.name,l.describes, p.name AS provincename,c.name AS cityname,a.name AS areaname,s.name AS systemname,s.pic AS pic
FROM location l LEFT JOIN province p ON l.provinceid = p.provinceid LEFT JOIN city c ON l.cityid = c.cityid LEFT JOIN area a ON l.areaid = a.areaid LEFT JOIN system s ON l.systemid = s.id
AND l.systemid = ${condition.systemId} AND l.areaid = ${condition.areaId} AND l.cityid = ${condition.cityId} AND l.provinceid = ${condition.provinceId}
ORDER BY l.systemid,l.provinceid,l.cityid,l.areaid ) vw
LIMIT ${page.currentNumber},${page.numberPerPage}

SELECT count(0)
FROM location l
LEFT JOIN province p ON l.provinceid = p.provinceid
LEFT JOIN city c ON l.cityid = c.cityid
LEFT JOIN area a ON l.areaid = a.areaid
LEFT JOIN system s ON l.systemid = s.id

    AND l.systemid = ${systemId}
    AND l.areaid = ${areaId}
    AND l.cityid = ${cityId}
    AND l.provinceid = ${provinceId}
